### 响应参数说明

``` typescript
export interface Ret {
  id: number;
  /**
   * 审核状态：dict/2001
   */
  auditStatus: number;
  /**
   * 发布者用户 id
   */
  userId: number;
  /**
   * 帖子内容 html
   */
  content: string;
  /**
   * 帖子封面
   */
  cover: string;
  /**
   * 帖子发布时间
   */
  postTime: number;
  /**
   * 帖子标题
   */
  title: string;
  /**
   * 帖子是否可见
   */
  isShow: number;
  /**
   * 帖子纯文本内容
   */
  text: string;
  /**
   * 帖子关联小组 id
   */
  groupId?: number;
  /**
   * 帖子关联标签列表
   */
  tags: {
    /**
     * 标签 id
     */
    id: number;
    /**
     * 标签名称
     */
    name: string;
  }[];
  /**
   * 赞踩数据
   */
  votes: {
    /**
     * 点赞总数
     */
    likes: number;
    /**
     * 点踩总数
     */
    dislikes: number;
    /**
     * 当前登录用户的操作
     *
     * 1: 已点赞, 2: 已点踩, 0: 取消（未）点赞踩
     */
    myVote: number;
  };
  /**
   * 评论数据
   */
  comments: {
    /**
     * 评论总数
     */
    count: number;
  };
  /**
   * 最近一次审核记录
   */
  lastLog: {
    /**
     * 审核不通过原因：dict/2001
     */
    reason: number | null;
    /**
     * 审核备注
     */
    remark?: string;
    /**
     * 审核状态：dict/2001
     */
    status: number;
  };
  /**
   * 发布者用户信息
   */
  user: {
    id: number;
    /**
     * 用户昵称
     */
    nickname: string;
    /**
     * 用户头像
     */
    avatarUrl?: string | null;
    /**
     * 用户信息审核状态：dict/2001
     */
    auditStatus: number;
  };
  /**
   * 关联小组信息
   */
  group?: {
    id: number;
    /**
     * 小组名称
     */
    name: string;
    /**
     * 小组头像
     */
    avatar: string;
    /**
     * 当前用户是否已加入小组
     */
    joined: boolean;
  };
  /**
   * 帖子关键词列表
   */
  keywords: string[];
}

